import React, { useState } from "react";
import { authService } from "../firebase";
import { getAuth, createUserWithEmailAndPassword, signInWithEmailAndPassword } from "firebase/auth";

const Auth = ()=>{
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const [newAcount, setNewAcount] = useState(true);
  const onSubmit = (e)=>{
    e.preventDefault();
    if(newAcount){
      //create Acount 회원가입
      authService.createUserWithEmailAndPassword( email, password)
    } else{
      //로그인
      authService.signInWithEmailAndPassword( email, password)

    }

  }
  const onChange = (e) =>{
    // let name = e.target.value;
    const {target:{name, value}} = e;
    
    if(name ==='email'){
      setEmail(value);
    } else{
      setPassword(value);
    }
  }
  return (
    <div>
      <form onSubmit={onSubmit}>
        <input name="email" type="email" placeholder="email" value={email} onChange={onChange}></input>
        <input name="password" type="password" placeholder="password" value={password} onChange={onChange}></input>
        <button>{newAcount ? "create Acount" : "Login"}</button>      
      </form>
    </div>
  )

}

export default Auth;
