import React, { useState } from "react";
import { collection, addDoc, serverTimestamp } from "firebase/firestore"; 
import { db } from "../firebase";

const Home = ()=>{
  const [post, setPost] = useState('');
    const onChange = (e)=>{
   //let value = e.target.value; // ECMA script 2012
   const {target:{value}} = e ; //ES6 문법
   setPost(value);
  }

  const onSubmit = async (e)=>{
    e.preventDefault();

    try{  //오류 점검
      const docRef = await addDoc(collection(db, "posts"), {  // posts는 컬렉션명
        content: post,
        date: serverTimestamp() //년월일시분초
      });
      console.log("Document written with ID: ", docRef.id);
      } catch(e) {
      console.log(e)
      }
  }
  console.log(post);

  return(
    <div>
       <form  onSubmit={onSubmit}>
        <input type="text" value={post} placeholder="포스트 쓰기" onChange={onChange}></input>
        <input type="submit" value="입력"></input>
       </form>
    </div>
   
  )
}
 

export default Home;
