import React, { useEffect, useState } from "react";
import { doc, onSnapshot, query, orderBy, collection, addDoc, serverTimestamp, getDocs  } from "firebase/firestore"; 
import { db } from "../firebase";
import Post from "../components/Post";

const Home = ({userObj})=>{
  const [post, setPost] = useState('');
  const [posts, setPosts] = useState([]);//조회된 글을 배열로 만듦

  const onChange = (e)=>{
   //let value = e.target.value; // ECMA script 2012
   const {target:{value}} = e ; //ES6 문법
   setPost(value);
  }

  const onSubmit = async (e)=>{
    e.preventDefault();

    try{  //오류 점검
      const docRef = await addDoc(collection(db, "posts"), {  // posts는 컬렉션명
        content: post,
        date: serverTimestamp(), //년월일시분초
        uid:userObj.userObj
      });
      console.log("Document written with ID: ", docRef.id);
      } catch(e) {
      console.log(e)
      }
  }
  console.log(post);


  /*
  const getPost = async ()=> {
    const querySnapshot = await getDocs(collection(db,  "posts"));
    querySnapshot.forEach((doc) => {
      const postObj = {
        ...doc.data(), id:doc.id
      }
      setPosts((prev)=>[postObj,...prev]);
    });
  }

  // const test = { id: 'id', title: 'title1', content:'content' }
  // const testcopy = { ...test, title: 'title2'}
  // console.log(testcopy);
*/

  useEffect(()=>{ //componentDidMount
    // getPost();
    const q = query(collection(db, "posts"), orderBy("date"));
    onSnapshot(q, (querySnapshot) => {
      // const cities = [];
      // querySnapshot.forEach((doc) => {
      //     cities.push(doc.data().name);
      // });
    });
  }, []) //한번만 작동

  console.log(posts)


  return(
    <div>
       <form  onSubmit={onSubmit}>
        <input type="text" value={post} placeholder="포스트 쓰기" onChange={onChange}></input>
        <input type="submit" value="입력"></input>
       </form>
       <hr/>
       <ul>
       {posts.map(item =>
          <Post
            key={item.id}
            postObj={item}
          />
        )}
       </ul>
    </div>
   
  )
}
 

export default Home;
