import React from "react";
import { doc, deleteDoc } from "firebase/firestore";
import { db } from "../firebase";

// await deleteDoc(doc(db, "cities", "DC"));  //문서삭제


const Post = ({postObj,userConfirm})=>(
 
  <li>
    <h4>{postObj.content}</h4>
    { 
      userConfirm && (
        <>
          <button>Delete</button>  
          <button>Edit</button>  
        </>
      )
    }
  </li>
);

export default Post;
