  const [isLoggedIn, setIsLoggedIn] = useState(authService.currentUser);
  //firebase 에 currentUser는 현재 유저의 유무
  const auth = getAuth();

  return (
    <AppRouter isLoggedIn={isLoggedIn}/>
  );
}

export default App;
